insert into films(name, description, year_of_issue, genre_id, minimal_age, duration_in_minutes, file_id)
values('Аватар: Путь воды', 'Долгожданный сиквел фантастического фильма-рекордсмена Джеймса Кэмерона.Амбициозный режиссер Джеймс Кэмерон не забыл про свое грандиозное детище — выращенный на незамысловатой канве «Покахонтас» фантастический во всех смыслах «Аватар» все-таки продолжается спустя целых 12 лет. Согласно сюжету главные герои — Джейк и Нейтири — обзавелись большой семьей и детьми. Однако помимо Нетей, Лоак и Туктири они вынужденно принимают еще одного ребенка — родившегося на военной базе планеты Пандора Майлза Сокорро. Мальчишка не смог отправиться на Землю, поскольку был еще слишком мал. Мать Нейтири, разумеется, видит в приемном сыне потомка своих заклятых врагов, некогда разрушивших ее дом и убивших отца. Предчувствие не обманывает героиню Зои Салдана: выясняется, что человеческие захватчики намерены довести дело до конца и завоевать Пандору. Это первое из четырех запланированных продолжений «Аватара», часть съемок которого прошли под водой.',
2022, 1, 12, 192, 1);
insert into films(name, description, year_of_issue, genre_id, minimal_age, duration_in_minutes, file_id)
values('Изумительный Морис', 'У кота Мориса есть два таланта: говорить и проворачивать аферы. Путешествуя с говорящими грызунами из города в город, он каждый раз «спасает» местных жителей от нашествия крыс (разумеется, за хорошую плату). Но попав в Дрянь-Блинцбург, Морис и его друзья понимают, что этот городок таит в себе нечто опасное. Теперь им предстоит сыграть в кошки-мышки с настоящим злом и разгадать главную загадку этого места — к изумлению всех живущих там двуногих.',
2022, 2, 6, 93, 2);
insert into films(name, description, year_of_issue, genre_id, minimal_age, duration_in_minutes, file_id)
values('Крушение', 'Пилоту Броуди Торрансу удаётся успешно посадить повреждённый штормом самолёт на враждебной территории. Вскоре выясняется, что уцелевшим угрожают воинствующие пираты, которые хотят захватить самолёт и его пассажиров в заложники. Пока идут поиски пропавшего самолёта, Броуди должен защитить своих пассажиров, пока не прибудет помощь.',
2023, 3, 16, 107, 3);
insert into films(name, description, year_of_issue, genre_id, minimal_age, duration_in_minutes, file_id)
values('Заговор дьявола', 'В биотехнологической лаборатории научились клонировать великих исторических деятелей. С тех пор были организованы подпольные аукционы клонов таких личностей как Микеланджело, Галилей и Вивальди. Однако сатанинский культ, который стоит за биолабораторией, интересуют не только деньги, поэтому они похищают Туринскую плащаницу. Заполучив таким образом ДНК, они планируют оплодотворить американку Лауру, чтобы она родила клона Христа, который они хотят преподнести Дьяволу.',
2022, 4, 18, 111, 4);
insert into films(name, description, year_of_issue, genre_id, minimal_age, duration_in_minutes, file_id)
values('На краю', 'Альпинистка Келли становится свидетелем убийства подруги. Она успевает снять происходящее на камеру, но ее замечают. Девушка отправляется к неприступной скале, надеясь там скрыться от убийц. Но она не подозревает, с какими трудностями ей придется столкнуться во время подъема на вершину и что ее убежище может стать настоящей ловушкой.',
2022, 5, 16, 86, 5);